// SETUP

@import url("https://fonts.googleapis.com/css2?family=Raleway:wght@400;500;600;700;800&display=swap");

* {
    font-family: "Raleway", sans-serif;
}

html {
    scroll-behavior: smooth;
}

body {
    margin: 0;
    // overflow-x: hidden;
}

p {
    margin: 0;
}

$bleu: #0065fc;
$bleu-clair: #deebff;
$gris: #f2f2f2;

.bleu {
    color: $bleu !important;
}

.gris {
    color: $gris !important;
}

.gris-bg {
    background-color: $gris !important;
}

.bold {
    font-weight: 800 !important;
}

.h1 {
    font-weight: 800;
    font-size: 1.4rem;
    letter-spacing: -0.04rem;
    margin-bottom: 10px;
    @media only screen and (max-width: 1200px) {
        font-size: 1.3rem;
    }
}

h2 {
    margin: 0;
}

.h4 {
    font-weight: 500;
    margin-top: 0;
}

.icon {
    color: black;
    font-size: 1.1rem;
}

.padding-responsive {
    padding-left: 50px;
    padding-right: 50px;
    @media only screen and (max-width: 1000px) {
        padding-left: 20px;
        padding-right: 20px;
    }
    &_full {
        @media only screen and (max-width: 1000px) {
            padding-left: 0;
            padding-right: 0;
        }
    }
}

a {
    text-decoration: none;
    color: black;
}

// CLASSES SPECIFIQUES

// NAV
.nav {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 50px;
    @media only screen and (max-width: 768px) {
        margin-bottom: 0;
    }
}

.menu {
    display: flex;
    align-items: center;
    justify-content: space-around;
    height: 100px;

    &-sections {
        position: relative;
        display: flex;
        align-items: center;
        height: 92px;
        &:before {
            z-index: -1;
            content: "";
            position: absolute;
            left: 10px;
            bottom: 0;
            height: 100%;
            width: 50%;
            border-top: 4px solid $bleu;
        }
        // &:hover .menu-sections:before {
        //     left: unset!important;
        //     right: 10px!important;
        // }
        @media only screen and (max-width: 768px) {
            display: none;
        }
        // .menu-title:last-child:hover .menu-sections:before {
        //     right: 10px;
        // }
    }
    &-sectionsPhone {
        position: relative;
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: space-around;
        &:before {
            z-index: -2;
            content: "";
            position: absolute;
            left: 0;
            bottom: -4px;
            height: 100%;
            width: 100%;
            border-bottom: solid $gris 4px;
        }
        .menu-title {
            width: 120px;
            text-align: center;
        }
        &:after {
            z-index: -1;
            content: "";
            position: absolute;
            left: 0;
            bottom: -4px;
            height: 100%;
            width: 50%;
            border-bottom: 4px solid $bleu;
        }
        @media only screen and (min-width: 768px) {
            display: none;
        }
    }

    &-title {
        text-decoration: none;
        color: black;
        padding: 50px;
        @media only screen and (max-width: 768px) {
            padding: 20px 0 20px 0;
        }
        &_active {
            color: $bleu;
            font-weight: 600;
        }
        &_sign {
            @media only screen and (max-width: 768px) {
                padding-right: 0;
            }
        }
    }
}

// HEADER
.header {
    margin-bottom: 50px;
    @media only screen and (max-width: 768px) {
        margin-top: 40px;
        margin-bottom: 40px;
    }
}

.recherche {
    height: 50px;
    display: flex;
    flex-direction: row;
    @media only screen and (max-width: 768px) {
        margin-top: 40px;
        width: 100%;
        border: solid $gris 2px;
        border-radius: 20px;
    }
    &-left {
        width: 55px;
        border-radius: 20px 0 0 20px;
        display: flex;
        align-items: center;
        justify-content: center;
    }
    &-input {
        border: solid $gris 2px;
        width: 230px;
        padding: 20px;
        &::placeholder {
            font-weight: 700;
            color: black;
            font-size: 1.2rem;
            letter-spacing: -0.04rem;
        }
        @media only screen and (max-width: 768px) {
            width: calc(100% - 110px);
            border: none;
        }
    }
    &-button {
        border: none;
        border-radius: 0 20px 20px 0;
        background-color: $bleu;
        width: 130px;
        color: white;
        font-weight: 600;
        font-size: 1.1rem;
        @media only screen and (max-width: 768px) {
            display: flex;
            align-items: center;
            justify-content: center;
            width: 55px;
            border-radius: 20px;
            padding: 0;
            // font-weight: initial;
            // font-size: initial;
        }
    }
    &-text {
        @media only screen and (max-width: 768px) {
            display: none;
        }
    }
    &-icon {
        @media only screen and (min-width: 768px) {
            display: none !important;
        }
    }
}

// FILTRES
.filtres {
    display: flex;
    flex-direction: row;
    align-items: center;
    flex-wrap: wrap;
    &-title {
        font-weight: 800;
        margin-right: 15px;
        @media only screen and (max-width: 1000px) {
            width: 100%;
            margin-bottom: 15px;
        }
    }
    &-button {
        margin-left: 15px;
        margin-right: 15px;
        display: flex;
        align-items: center;
        border-radius: 30px;
        height: 48px;
        border: solid $gris 3px;
        cursor: pointer;
        @media only screen and (min-width: 1000px) {
            &:hover {
                transition: all 0.1s ease-out;
            }
            &:hover .filtres-button-icon {
                transition: all 0.1s ease-out;
                // font-size: 23px;
                transform: scale(1.1);
            }
        }
        @media only screen and (max-width: 1000px) {
            margin-bottom: 15px;
        }
        @media only screen and (max-width: 768px) {
            margin-left: 5px;
            margin-right: 5px;
        }
        &-icon {
            background-color: $bleu-clair;
            width: 50px;
            height: 50px;
            border-radius: 25px;
            display: flex;
            align-items: center;
            justify-content: center;
            margin-left: -2px;
            font-size: 20px;
        }
        &-text {
            margin-left: 15px;
            margin-right: 15px;
            margin-top: 0;
            margin-bottom: 0;
        }
    }
}

// INFO
.info {
    display: flex;
    align-items: center;
    flex-direction: row;
    margin-top: 30px;
    margin-bottom: 30px;
    &-icon {
        font-size: 13px;
        color: $bleu;
        display: flex;
        align-items: center;
        justify-content: center;
        width: 25px;
        height: 25px;
        border: solid 2px $gris;
        border-radius: 100%;
        margin-right: 15px;
    }
    &-text {
        @media only screen and (max-width: 768px) {
            width: calc(100% - 50px);
        }
    }
}

// HEBERGEMENTS
.hebergements {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    max-width: 100%;
    @media only screen and (max-width: 1000px) {
        flex-direction: column-reverse;
    }
    &-marseille {
        padding: 20px;
        padding-top: 40px;
        background-color: $gris;
        border-radius: 20px;
        width: 70%;
        margin-right: 30px;
        @media only screen and (max-width: 1000px) {
            width: calc(100% - 40px);
            background-color: transparent;
        }
        @media only screen and (max-width: 768px) {
            // flex-direction: column;
        }
        & .containerVisuel {
            margin-bottom: 20px;
        }
    }
    &-populaires {
        padding: 20px;
        padding-top: 40px;
        padding-bottom: 0;
        width: 25%;
        background-color: $gris;
        border-radius: 20px;
        @media only screen and (max-width: 1000px) {
            width: calc(100% - 40px);
            border-radius: 0;
        }
    }
}

.containerVisuel {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    margin-top: 40px;
    margin-bottom: 10px;
    @media only screen and (max-width: 768px) {
        margin-top: 20px;
    }
}

.visuel {
    box-shadow: 0 0 15px rgb(194, 194, 194);
    background-color: white;
    width: 30%;
    height: 220px;
    border-radius: 20px;
    padding: 5px;
    margin-bottom: 2vw;
    &_populaires {
        box-shadow: 0 0 15px rgb(194, 194, 194);
        width: 100%;
        height: 160px;
        background-color: white;
        border-radius: 20px;
        margin-bottom: 2vw;
        padding: 5px;
        display: flex;
        & img {
            width: 40%;
            object-fit: cover;
            object-position: center;
            border-radius: 20px 0 0 20px;
            height: 100%;
        }
        .description {
            padding-left: 20px;
        }
        @media only screen and (max-width: 768px) {
            width: 100%;
            margin-bottom: 1rem;
        }
    }
    & img {
        width: 100%;
        object-fit: cover;
        object-position: center;
        border-radius: 20px 20px 0 0;
        height: 60%;
    }
    @media only screen and (max-width: 768px) {
        width: 100%;
        margin-bottom: 1rem;
    }
}

.description {
    padding: 10px;
    position: relative;
    @media only screen and (max-width: 1200px) {
        font-size: 13px;
    }
    & p {
        margin-bottom: 5px;
    }
}

.notation {
    &-populaires {
        position: absolute;
        bottom: 0;
        margin-bottom: 10px;
        font-size: 13px;
    }
}

// ACTIVITES
.activites {
    margin-top: 50px;
    &-flex {
        margin-top: 30px;
        height: 500px;
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        &-col {
            display: flex;
            flex-direction: column;
            width: 25%;
            margin-right: 30px;
            justify-content: space-between;
            @media only screen and (max-width: 768px) {
                width: 100%;
            }
            &:last-child {
                margin-right: 0;
            }
            & img {
                border-radius: 20px 20px 0 0;
                height: calc(100% - 50px);
                width: 100%;
                object-fit: cover;
            }
        }
        @media only screen and (max-width: 768px) {
            flex-direction: column;
            display: inline;
        }
    }
    &-visuel {
        border-radius: 20px;
        box-shadow: 0 0 15px rgb(194, 194, 194);
        position: relative;
        & p {
            position: absolute;
            bottom: 15px;
            margin-left: 15px;
            @media only screen and (max-width: 1200px) {
                font-size: 13px;
                bottom: 17px;
                margin-left: 13px;
                margin-right: 13px;
            }
        }
        @media only screen and (max-width: 768px) {
            margin-bottom: 1rem;
        }
        &_1 {
            height: 100%;
            @media only screen and (max-width: 768px) {
                height: 220px;
                margin-top: 20px;
            }
        }
        &_2 {
            height: 62%;
            @media only screen and (max-width: 768px) {
                height: 220px;
            }
        }
        &_3 {
            height: 30%;
            @media only screen and (max-width: 768px) {
                height: 220px;
            }
        }
        &_4 {
            height: 100%;
            @media only screen and (max-width: 768px) {
                height: 220px;
            }
        }
        &_5 {
            height: 46%;
            @media only screen and (max-width: 768px) {
                height: 220px;
            }
        }
        &_6 {
            height: 46%;
            @media only screen and (max-width: 768px) {
                height: 220px;
            }
        }
    }
}

// FOOTER

.footer {
    position: relative;
    margin: 0;
    margin-top: 50px;
    padding-top: 40px;
    display: flex;
    background-color: $gris;
    flex-wrap: wrap;
    &-col {
        width: 33%;
        margin-bottom: 50px;
        @media only screen and (max-width: 1000px) {
            width: 50%;
        }
        @media only screen and (max-width: 768px) {
            width: 100%;
        }
    }
    &-title {
        font-weight: 800;
        margin-bottom: 1.3rem !important;
    }
    p {
        margin-bottom: 0.8rem;
    }
}
